import React , { useState, useEffect } from 'react';
import UserNavbar from '../../components/Navbar/UserNavbar';
import './Profile.css';
import { Row, Col } from 'react-bootstrap';
import { Image } from 'cloudinary-react';
import axios from 'axios';
import BASE_URL from '../../config';

const Profile = () => {

    const [postsArray, setPostsArray] = useState([]);
    const [userDetails, setUserDetails] = useState({
        username: '',
        fullName: '',
        age: '',
        gender: ''
    });

    useEffect(()=>{

        const username = JSON.parse(localStorage.getItem('user-info')).username;
        axios.get(`${BASE_URL}/upload/userPosts`, {
            params: {
                username: username,
                id: 1
            }
        })
        .then((res)=>{
            console.log(res);
            setPostsArray(res.data);
        })
        .catch((err)=>{
            console.log(err);
        })

    }, []);


    return (
        <div>
            <UserNavbar iconName="profile" />
            <div>
                <header>
                    <Row className="userArea">
                        <Col xs={12} className="user">
                            <div>
                                @{userDetails.username}
                            </div>
                            <div>
                                {userDetails.fullName}, {userDetails.age}
                            </div>
                        </Col>

                    </Row>
                </header>
            </div>
            <div>
                POSTS ARRAY With Delete option
                <Row className="bg-primary">
                        <div className="Feed">
                        {   
                            postsArray.map((el)=>{
                                return (
                                    <Col xs={12} md={6} className="Post" key={el.id}>
                                        <div className="Image">
                                            <Image 
                                                cloudName="du7o5hpad" 
                                                publicId= {el.image}
                                                />
                                        </div>
                                        <div className="Content">
                                            <div className="title">{el.title} / by @{el.author}</div>
                                            <div className="description"> {el.description} </div>
                                            <div>
                                                <span
                                                style = {{
                                                    display: "inline-block",
                                                    marginLeft: "5px",
                                                    fontSize: "20px"
                                                }}
                                                >{el.likes}</span>
                                            </div>
                                        </div>
                                    </Col>
                                )
                            })
                        }

                    </div>
                </Row>
            </div>
        </div>
    )
}

export default Profile;
