import React, { useState, useEffect } from 'react';
import classes from './Feed.module.css';
import Footer from '../../components/Footer/Footer';
import USerNavbar from '../../components/Navbar/UserNavbar';
import { Image } from 'cloudinary-react';
import axios from 'axios';
import BASE_URL from '../../config';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';

const Feed = () => {

    const [postsArray, setPostsArray] = useState([]);
    const [likes, setLikes] = useState(0);

    useEffect(()=>{

        axios.get(`${BASE_URL}/upload`)
        .then((response)=>{
            setPostsArray(response.data);
        })
        .catch((err)=>{
            console.log(err);
        });

    },[]);

    const likePost = (id)=>{
        const localStr = JSON.parse(localStorage.getItem('user-info'));
        const userLiking = localStr.username;
        axios.post(`${BASE_URL}/upload/like`, {
            userLiking: userLiking,
            postID: id
        })
        .then(()=>{
            console.log("You liked this post");
        })
    }

    return (
        <div>
            <USerNavbar iconName="home" />
            <div className={classes.Feed}>
                {   
                    postsArray.map((el)=>{
                        return (
                            <div className={classes.Post} key={el.id}>
                                <div className={classes.Image}>
                                    <Image 
                                        cloudName="du7o5hpad" 
                                        publicId= {el.image}
                                        />
                                </div>
                                <div className={classes.Content}>
                                    <div className={classes.title}>{el.title} / by @{el.author}</div>
                                    <div className={classes.description}> {el.description} </div>
                                    <ThumbUpAltIcon 
                                        className={classes.customIcon} 
                                        onClick= {
                                            ()=>{
                                                likePost(el.id);
                                            }
                                        }
                                        />
                                </div>
                            </div>
                        )
                    })
                }

            </div>


            <Footer />
        </div>
    )
}


export default Feed;
