import React, { useState } from 'react';
import classes from './Register.module.css';
import Navbar from '../../components/Navbar/Navbar';
import { Fragment } from 'react';
import { Row, Col, Form } from 'react-bootstrap';
import Footer from '../../components/Footer/Footer';
import { FaEyeSlash } from 'react-icons/fa';
import axios from 'axios';
import BASE_URL from '../../config';


const Register = () => {

    const [fullName, setFullName] = useState('');
    const [age, setAge] = useState('');
    const [gender, setGender] = useState('Male');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const [type, setType] = useState('password');
    const toggleType = () => {
        setType((prevState) => {
        if (prevState === 'password')
            return 'text';
        return 'password';
        })
    }

    const fullNameChangeHandler = (e)=>{
        const val = e.target.value;
        setFullName(val);
    }

    const ageChangeHandler = (e)=>{
        const val = e.target.value;
        setAge(val);
    }

    const genderChangeHandler = (e)=>{
        const val = e.target.value;
        setGender(val);
    }


    const usernameChangeHandler = (e)=>{
        const val = e.target.value;
        setUsername(val);
    }

    const passwordChangeHandler = (e)=>{
        const val = e.target.value;
        setPassword(val);
    }


    const onSubmitHandler = (e)=> {
        e.preventDefault();
        axios.post(`${BASE_URL}/user/register`);
    }

    return (
        <Fragment>
            <Navbar iconName="register" />
            <div className={classes.register}>
                <h1>Registeration</h1>
            <div className={classes.registerForm}>
                <Form onSubmit={onSubmitHandler}>
                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Full Name</Form.Label>
                        <Form.Control
                            type="text"
                            name="fullName"
                            value={fullName}
                            placeholder="Full Name..."
                            className="shadow-none"
                            onChange={fullNameChangeHandler}
                        />
                    </Form.Group>

                    <Row>
                    <Col xs={6}>
                        <Form.Group>
                            <Form.Label 
                            className={classes.formLabel}
                            >Age</Form.Label>
                            <Form.Control
                                style={{
                                marginLeft: "45px"
                                }}
                                type="text"
                                name="fullName"
                                className="shadow-none"
                                value={age}
                                onChange={ageChangeHandler}
                            />
                        </Form.Group>
                    </Col>

                    <Col xs={6}>
                        <Form.Group>
                            <Form.Label 
                            className={classes.formLabel}
                            style={{
                                marginLeft: "0"
                            }}
                            >Gender</Form.Label>
                            <Form.Control
                                as="select"
                                name="gender"
                                className="shadow-none"
                                style={{
                                    width: "79%",
                                    paddingLeft: "6px"
                                }}
                                value={gender}
                                onChange={genderChangeHandler}
                            >
                                <option>Male</option>
                                <option>Female</option>
                            </Form.Control>
                        </Form.Group>
                    </Col>
                    </Row>

                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Username</Form.Label>
                        <Form.Control
                            type="text"
                            name="username"
                            value={username}
                            placeholder="Username..."
                            className="shadow-none"
                            onChange={usernameChangeHandler}
                        />
                    </Form.Group>

                    <Form.Group className={classes["form-group--password"]}>
                        <Form.Label
                        className={classes.formLabel}
                        >Password</Form.Label>
                        <Form.Control
                            type={type}
                            name="password"
                            value={password}
                            placeholder="Password..."
                            className="shadow-none"
                            onChange={passwordChangeHandler}
                        />
                        <FaEyeSlash className={classes.passwordIcon} onClick={toggleType} />
                    </Form.Group>
                    <div className="text-center">
                        <button type="submit" className={classes["square_btn"]}>Register</button>
                    </div>
                </Form>
            </div>
            </div>
            <Footer />
        </Fragment>
    )
}
export default Register;
