import React, { useState } from 'react';
import classes from './Login.module.css';
import Navbar from '../../components/Navbar/Navbar';
import { Fragment } from 'react';
import { Row, Col, Form, InputGroup } from 'react-bootstrap';
import Footer from '../../components/Footer/Footer';
import { FaEyeSlash } from 'react-icons/fa';

const Login = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');
    const [type, setType] = useState('password');
    const toggleType = () => {
        setType((prevState) => {
        if (prevState === 'password')
            return 'text';
        return 'password';
        })
    }

    const usernameChangeHandler = (e)=>{
        const val = e.target.value;
        setUsername(val);
    }

    const passwordChangeHandler = (e)=>{
        const val = e.target.value;
        setPassword(val);
    }

    const fullNameChangeHandler = (e)=>{
        const val = e.target.value;
        setFullName(val);
    }

    const onSubmitHandler = (e)=> {
        e.preventDefault();
    }

    return (
        <Fragment>
            <Navbar iconName="login" />
            <div className={classes.login}>
                <h1>Login</h1>
            <div className={classes.loginForm}>
                <Form onSubmit={onSubmitHandler}>
                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Full Name</Form.Label>
                        <Form.Control
                            type="text"
                            name="fullName"
                            value={fullName}
                            placeholder="Full Name..."
                            className="shadow-none"
                            onChange={fullNameChangeHandler}
                        />
                    </Form.Group>


                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Username</Form.Label>
                        <Form.Control
                            type="text"
                            name="username"
                            value={username}
                            placeholder="Username..."
                            className="shadow-none"
                            onChange={usernameChangeHandler}
                        />
                    </Form.Group>

                    <Form.Group className={classes["form-group--password"]}>
                        <Form.Label
                        className={classes.formLabel}
                        >Password</Form.Label>
                        <Form.Control
                            type={type}
                            name="password"
                            value={password}
                            placeholder="Password..."
                            className="shadow-none"
                            onChange={passwordChangeHandler}
                        />
                        <FaEyeSlash className={classes.passwordIcon} onClick={toggleType} />
                    </Form.Group>
                    <div className="text-center">
                        <button type="submit" className={classes["square_btn"]}>Login</button>
                    </div>
                </Form>
            </div>
            </div>
            <Footer />
        </Fragment>
    )
}
export default Login;
