import React, { useState } from 'react';
import UserNavbar from '../../components/Navbar/UserNavbar';
import classes from './Upload.module.css';
import Footer from '../../components/Footer/Footer';
import { Form } from 'react-bootstrap';
import axios from 'axios';
import BASE_URL from '../../config';
import { useHistory } from 'react-router-dom';

const Upload = () => {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState([]);
    const history = useHistory();

    const onSubmitHandler= (e)=>{
        e.preventDefault();

        const formData = new FormData();
        formData.append("file",image[0]);
        formData.append("upload_preset", "w9wt7bfe");

        axios.post("https://api.cloudinary.com/v1_1/du7o5hpad/image/upload", formData)
        .then((response)=>{
            console.log(response);
            const fileName = response.data.public_id;
                axios.post(`${BASE_URL}/upload`,{   
                    title: title, 
                    description: description, 
                    image: fileName
                })
                .then((res)=>{
                    console.log(res);
                })
                .catch((err)=>{
                    console.log(err);
                })
        })
        .catch((err)=>{
            console.log(err);
        })
    }   

    return (
        <div>
            <UserNavbar iconName="upload" />
            <div className={classes.upload}>
            <div className={classes.uploadForm}>
                <h1>Create a post!</h1>

                <Form onSubmit={onSubmitHandler}>
                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Title</Form.Label>
                        <Form.Control
                            type="text"
                            name="title"
                            placeholder="Title.."
                            className="shadow-none"
                            value={title}
                            onChange={
                                (e)=>{
                                    setTitle(e.target.value);
                                }
                            }
                        />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label 
                        className={classes.formLabel}
                        >Description</Form.Label>
                        <Form.Control
                            type="text"
                            name="description"
                            placeholder="Description.."
                            className="shadow-none"
                            value={description}
                            onChange={
                                (e)=>{
                                    setDescription(e.target.value);
                                }
                            }
                        />
                    </Form.Group>

                    <input 
                        type="file" 
                        onChange={
                            (e)=>{
                                setImage(e.target.files);
                            }
                        }
                    />


                    <div className="text-center">
                        <button type="submit" className={classes["square_btn"]}>Create</button>
                    </div>
                </Form>
            </div>
            </div>
            <Footer />
        </div>
    )
}
export default Upload;
