import React , { useState, useEffect } from 'react';
import UserNavbar from '../../components/Navbar/UserNavbar';
import './Profile.css';
import { Row, Col } from 'react-bootstrap';
import { Image } from 'cloudinary-react';
import axios from 'axios';
import BASE_URL from '../../config';
import { MdDelete } from "react-icons/md";



const Profile = () => {

    const [postsArray, setPostsArray] = useState([]);
    const [userDetails, setUserDetails] = useState({
        username: '',
        fullName: '',
        age: '',
        gender: ''
    });

    useEffect(()=>{

        const username = JSON.parse(localStorage.getItem('user-info')).username;
        axios.get(`${BASE_URL}/upload/userPosts`, {
            params: {
                username: username
            }
        })
        .then((res)=>{
            console.log(res);
            setPostsArray(res.data);
        })
        .catch((err)=>{
            console.log(err);
        })

        axios.get(`${BASE_URL}/upload/userDetails`,{
            params: {
                username: username
            }
        })
        .then((res)=>{
            console.log("The res", res);
            setUserDetails(()=>{
                return {
                    username: res.data[0].username,
                    fullName: res.data[0].fullName,
                    age: res.data[0].age,
                    gender: res.data[0].gender
                }
            })
        })
        .catch((err)=>{
            console.log(err);
        })

    }, []);


    return (
        <div>
            <UserNavbar iconName="profile" />
            <div>
                <header>
                    <Row className="userArea">
                        <Col xs={12} className="user">
                            <div>
                                @{userDetails.username}
                            </div>
                            <div>
                                {userDetails.fullName}, {userDetails.age}
                            </div>
                        </Col>

                    </Row>
                </header>
            </div>
            <div>
                        <div className="Feed">
                        {   
                            postsArray.map((el)=>{
                                return (
                                    <div xs={12} md={6} className="Post" key={el.id}>
                                        <div className="Image">
                                            <Image 
                                                cloudName="du7o5hpad" 
                                                publicId= {el.image}
                                                />
                                        </div>
                                        <div className="Content">
                                            <div className="title">{el.title} / by @{el.author}</div>
                                            <div className="description"> {el.description} </div>
                                            <div className="d-flex justify-content-between">
                                                <span
                                                style = {{
                                                    display: "inline-block",
                                                    marginLeft: "5px",
                                                    fontSize: "20px"
                                                }}
                                                >{el.likes}</span>

                                                <span
                                                style = {{
                                                    display: "inline-block",
                                                    marginRight: "5px",
                                                }}
                                                >
                                                    <MdDelete className="delete--icon" />
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        }

                    </div>
            </div>
        </div>
    )
}

export default Profile;
