import React, { useEffect, useState} from 'react';
import UserNavbar from '../../components/Navbar/UserNavbar';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import BASE_URL from '../../config';
import Footer from '../../components/Footer/Footer';
import { Row, Col, Form} from 'react-bootstrap';
import './IndividualPost.css';
import { Image } from 'cloudinary-react';

const IndividualPost = () => {

    const params = useParams();

    const [postArray, setPostArray] = useState([]);

    useEffect(()=>{
        const postID = params.postID;

        axios.get(`${BASE_URL}/upload/getPost`, {
            params: {
                postID: postID
            }
        })
        .then((res)=>{
            console.log(res);
            setPostArray(res.data[0]);
        })
        .catch((err)=>{
            console.log(err);
        })


    },[params.postID]);

    return (
        <div>
                <UserNavbar iconName="posts" />

                <div className="upload">
                    <Row>

                    <Col>
                            <div className="Post">
                                        <div className="Image">
                                        <Image 
                                            cloudName="du7o5hpad" 
                                            publicId= {postArray.image}
                                        />
                                        </div>
                                        <div className="Content">
                                            <div className="title">  {postArray.title}/ by @{postArray.author}</div>
                                            <div className="description"> {postArray.description} </div>
                                        </div>
                            </div>
                    </Col>

                    <Col>
                            <div className="Post Comments">
                                     <h1 className="comments-heading">Comments</h1>
                                     <div className="bg-primary comments-block">
                                            <div>@shagun2</div>
                                            <div>Wow, really nice picture!</div>
                                     </div>
